mapper.select:
SELECT 
FROM product
WHERE is_active = true

    AND price >= #{minPrice}


    AND price <= #{maxPrice}

ORDER BY price ASC
LIMIT #{limit} OFFSET #{offset}

SELECT 
FROM product
WHERE is_active = true

    AND (name ILIKE CONCAT('%', #{keyword}, '%') OR description ILIKE CONCAT('%', #{keyword}, '%'))


    AND price >= #{minPrice}


    AND price <= #{maxPrice}

ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}
