mapper.insert:
INSERT INTO vip_level (
    name, min_spending, discount_rate, points_multiplier,
    free_shipping_threshold, benefits, is_active, sort_order
) VALUES (
    #{name}, #{minSpending}, #{discountRate}, #{pointsMultiplier},
    #{freeShippingThreshold}, #{benefits}, 
    COALESCE(#{isActive}, true), #{sortOrder}
)
